    private void AddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddActionPerformed
        if (PathDisplay.getText().isBlank()) {
            JOptionPane.showMessageDialog(null, "No se ha agregado ning√∫n elemento.");
        }
        else{ 
            Functions functions = new Functions();
            String w = functions.readText(PathDisplay.getText());
            InfoObject summary = functions.createObjects(w);
            if (!GlobalVariables.getTable().search2(summary)) {
                GlobalVariables.getTable().insert(summary);
                GlobalVariables.getList().insertEnd(summary.getTitle());
                GlobalVariables.getObjects().insertEnd2(summary);
//                GlobalVariables.getObjects().insertEnd2(summary);
                functions.writeText(PathDisplay.getText());
            }
            else{
                JOptionPane.showMessageDialog(null, "Ya el resumen ha sido agregado.");
            } 
        }


    }//GEN-LAST:event_AddActionPerformed

